Fase_2_ATO %rullaggio
du:
Fase_ATO = 2;
% devo mettere uno switch sulla manetta che tramite FASE_ATO la incrementa gradualmente fino al massimo e poi switcho sul controllore di velocita
psi_ref_selector=0;
psi_ref = psi_hold;
phi_ref = 0;
h_ref = 0;
eq_rot = 2;
Tas_ref = 25;

Fase_1_ATO
du:
Fase_ATO = 1;
manetta_ref = 0.1;
psi_ref_selector=0;
h_ref = 0;
psi_ref = psi_hold;
phi_ref = 0;
eq_rot = 2;

Fase_3_ATO  %rotazione
du:
Fase_ATO = 3;
psi_ref_selector=0;
theta_ground_selector = 0;
psi_ref = psi_hold;
phi_ref = 0;
h_ref = 0;
pitch_ref = 10 + theta_ground_hold;
Tas_ref = 25;
%devo passare al controllore di pitch, prima davo direttamente l'angolo dell'equilibratore a picchiare (ora devo fare la rotazione)

Fase_4_ATO  %salita
du:
Fase_ATO = 4;
psi_ref_selector=0;
psi_ref = psi_hold;
h_ref = 35;
RC_ref = 3;
Tas_ref = 25;
%devo passare al controllore di quota, prima controllavo il pitch
%devo passare al controllore di roll canonico, prima fissavo il riferimento di roll = 0

eml.P:
function [x_f_out,h_f_out,k_x_out,h_c_out,no_solution] = fcn(reset,gamma,xg0)



x_f=-200;
h_f=1.5;
%x_f=-300;
%h_f=10;
k_x=0.01;
h_c=-2;
x_td=5;
h_td_dot=-0.1;
x_td_dot=18;
no_solution = 0;

% x_f=-200;
% h_f=5;
% %x_f=-300;
% %h_f=10;
% k_x=0.01;
% h_c=-1;
% x_td=50;
% h_td_dot=-0.15;
% x_td_dot=18;
% no_solution = 0;

% F = [f(x_f,h_f,gamma,xg0); g(h_c,h_f,k_x,gamma); r(h_c,h_f,k_x,x_f,x_td); s(h_c,h_f,k_x,x_f,x_td,h_td_dot,x_td_dot)];
% 
% J = [ df1() df2(gamma) df3() df4();
%       dg1(k_x) dg2() dg3(h_f,h_c) dg4(k_x);
%       dr1(k_x,x_td,x_f) dr2(k_x,x_td,x_f) dr3(k_x,x_td,x_f,h_f,h_c) dr4();
%       ds1(k_x,x_td,x_f,x_td_dot) ds2(k_x,x_td,x_f,x_td_dot,h_f,h_c) ds3(k_x,x_td,x_f,x_td_dot,h_f,h_c) ds4(k_x,x_td,x_f,x_td_dot)]

persistent x x_new cycle_count eps

if (isempty(x) ||  reset == 0)
x = [h_f; x_f; k_x; h_c];
end

if (isempty(x_new) || reset == 0)
x_new = zeros(4,1);
end

if (isempty(eps) || reset == 0)
eps = ones(1,4);
end

if (isempty(cycle_count) || reset == 0)
cycle_count = 0;
end




while ~ (eps(1) < 0.1 && eps(2) < 0.1 && eps(3) < 0.1 && eps(4) < 0.1 )

F = [f(x(2),x(1),gamma,xg0); g(x(4),x(1),x(3),gamma); r(x(4),x(1),x(3),x(2),x_td); s(x(4),x(1),x(3),x(2),x_td,h_td_dot,x_td_dot)];

J = [ df1() df2(gamma) df3() df4();
      dg1(x(3)) dg2() dg3(x(1),x(4)) dg4(x(3));
      dr1(x(3),x_td,x(2)) dr2(x(3),x_td,x(2),x(1),x(4)) dr3(x(3),x_td,x(2),x(1),x(4)) dr4();
      ds1(x(3),x_td,x(2),x_td_dot) ds2(x(3),x_td,x(2),x_td_dot,x(1),x(4)) ds3(x(3),x_td,x(2),x_td_dot,x(1),x(4)) ds4(x(3),x_td,x(2),x_td_dot)] ; 


x_new = x - J^(-1)*F;

eps(1) = abs(x_new(1)-x(1));
eps(2) = abs(x_new(2)-x(2));
eps(3) = abs(x_new(3)-x(3));
eps(4) = abs(x_new(4)-x(4));


x = x_new;

cycle_count = cycle_count + 1;

if cycle_count > 10
   no_solution = 1; 
   break; 
end

end

if (eps(1) < 0.1 && eps(2) < 0.1 && eps(3) < 0.1 && eps(4) < 0.1 )

h_f_out = x(1);
x_f_out = x(2);
k_x_out = x(3);
h_c_out = x(4);

else

h_f_out = 0;
x_f_out = 0;
k_x_out = 0;
h_c_out = 0;    
end

function y = f(x_f,h_f,gamma,xg0)                 %inizio equazioni sistema non lineare
y = h_f+(x_f-xg0)*tan(gamma);
end

function y = g(h_c,h_f,k_x,gamma)
y = (h_f-h_c)*k_x-tan(gamma);
end

function y = r(h_c,h_f,k_x,x_f,x_td)
y = h_c + (h_f-h_c)*exp(-k_x*(x_td-x_f));
end

function y = s(h_c,h_f,k_x,x_f,x_td,h_td_dot,x_td_dot)
y = h_td_dot + (h_f-h_c)*k_x*x_td_dot*exp(-k_x*(x_td-x_f));        
end                                                                   % fine equazioni sistema non lineare

function y = df1()                                                    %inizio derivate
y = 1;
end

function y = df2(gamma)
y = tan(gamma);
end

function y = df3()
y = 0;
end

function y = df4()
y = 0;
end

function y = dg1(k_x)
y = k_x;
end

function y = dg2()
y = 0;
end

function y = dg3(h_f,h_c)
y = h_f-h_c;
end

function y = dg4(k_x)
y = -k_x;
end

function y = dr1(k_x,x_td,x_f)
y = exp(-k_x*(x_td-x_f));
end

function y = dr2(k_x,x_td,x_f,h_f,h_c)
y = (h_f-h_c)*k_x*exp(-k_x*(x_td-x_f));
end

function y = dr3(k_x,x_td,x_f,h_f,h_c)
y = (h_f-h_c)*(x_f-x_td)*exp(-k_x*(x_td-x_f));
end

function y = dr4()
y = 1;
end

function y = ds1(k_x,x_td,x_f,x_td_dot)
y = k_x*x_td_dot*exp(-k_x*(x_td-x_f));
end

function y = ds2(k_x,x_td,x_f,x_td_dot,h_f,h_c)
y = (h_f-h_c)*k_x^2*x_td_dot*exp(-k_x*(x_td-x_f));
end

function y = ds3(k_x,x_td,x_f,x_td_dot,h_f,h_c)
y = (h_f-h_c)*x_td_dot*exp(-k_x*(x_td-x_f))+(h_f-h_c)*k_x*x_td_dot*(x_f-x_td)*exp(-k_x*(x_td-x_f));
end

function y = ds4(k_x,x_td,x_f,x_td_dot)
y = -k_x*x_td_dot*exp(-k_x*(x_td-x_f));
end



end

function [rate_l_up, P_refer, rate_l_dw]  = fcn( Cal_alt, P,P_int,P_cal)
%#codegen

persistent P_ref rl_up rl_dw

if isempty(P_ref)

    P_ref = 101325;

end

if isempty(rl_up)

    rl_up = 300;

end

if isempty(rl_dw)

    rl_dw = -300;

end

if Cal_alt == 1

    P_ref = P;

end

if P_int == 1

   P_ref = P_cal; 
   rl_up = 20;
   rl_dw = -20;

end



P_refer = P_ref;

rate_l_up = rl_up;
rate_l_dw = rl_dw;

function [status,messaggio]  = LETTURA_NMEA(byte_pack)

persistent message counter start_found end_found

status = false;       %ho aggiunto questo
messaggio = uint8(zeros(1,80)) ;
pos_end = uint8(0) ;
M = uint8(length(byte_pack)) ;


if isempty(end_found) 
    end_found = false ;
end 
if isempty(start_found) 
     start_found = false ;
end 
if isempty (counter) 
    counter = uint8(0) ;
end 
if isempty(message)
     message = uint8(zeros(1,80)) ;
end



if start_found == 0 
    for i = uint8(1) : M  
        if byte_pack(i) == '$' 
            message(1:M - (i-1)) = byte_pack(i:end) ;
            counter = M - (i-1) ; 
            start_found = true ;
            break
        end
    end


elseif start_found == 1
    for i = uint8(1) : M  
        if byte_pack(i) == 13 
            end_found = true ;
            pos_end = i ;
            break
        end
    end

    if end_found == 1
        message(counter + 1 : counter + pos_end) = byte_pack(1:pos_end) ;
        status = true;       %ho aggiunto questo
        messaggio = message ;
        end_found = false ;
        counter = M - pos_end ;
        if pos_end == M 
            start_found = false ; 
            message = uint8(zeros(1,80)) ;
        else  
            start_found = true ;
            message(1:(M - pos_end)) = byte_pack(pos_end + 1 : end) ;
            message(M - pos_end +1 : 80) = uint8(zeros(1,80 - (M - pos_end) )) ;
        end


    else 
    message(counter + 1 : M + counter ) = byte_pack ;
    counter = M + counter  ;

    end






end

function [Lat,Long,V,Magnetic_declination,heading,fix_mode,n_sat,h_mean_sea,h_WGS84,indice_dimario] = parser_NMEA(messaggio,out_prec)


messaggio = char(messaggio) ;

comma_gga = uint8(zeros(1,14));
comma_rmc = uint8(zeros(1,12));
star = uint8(0);
dollar = uint8(0);
find_dollar = uint8(0);
find_comma = uint8(0);
message_type = uint8(zeros(1,5));
indice_dimario = uint8(0);





for i=1:length(messaggio)
   if messaggio(i) == '$'
       find_dollar = find_dollar + uint8(1);
   end
   if messaggio(i) == ','
       find_comma = find_comma + uint8(1);
   end
end

if (find_dollar == 1 && (find_comma == 12 || find_comma == 14))               % controllo se il numero di $ e , sono corretti all'interno di messaggio
                                                                             % altrimenti il messaggio è corrotto ed in output mando i messaggi
                                                                             % precedenti
dollar= find(messaggio == '$',1);

mess_type = messaggio(dollar(1) + 1 : dollar(1) + 5);                              % è il nome del messaggio arrivato

% decodifica mesaggio RMC

if mess_type == 'GPRMC'
comma_rmc = find(messaggio == ',');                                              % restituisce le posizioni delle virgole all'interno del messaggio
star = find(messaggio == '*');                                               % restituisce la posizione dell'asterisco all'interno del messaggio
dollar = find(messaggio == '$');                                             % restituisce la posizione del dollaro all'interno del messaggio

Lat_1 = str2double(messaggio(comma_rmc(3) + 1 : comma_rmc(3) + 2));
Lat_2 = str2double(messaggio(comma_rmc(3) + 3 : comma_rmc(4) - 1))/60;
Lat = Lat_1 + Lat_2;
if messaggio(comma_rmc(4) + 1) == 'S'
Lat = -Lat; 
end

Long_1 = str2double(messaggio(comma_rmc(5) + 1 : comma_rmc(5) + 3));
Long_2 = str2double(messaggio(comma_rmc(5) + 4 : comma_rmc(6) - 1))/60;
Long = Long_1 + Long_2;
if messaggio(comma_rmc(6) + 1) == 'W'
Long = -Long; 
end

V = str2double(messaggio(comma_rmc(7) + 1 : comma_rmc(8) - 1)); 

heading = str2double(messaggio(comma_rmc(8) + 1 : comma_rmc(9) - 1));

Magnetic_declination = str2double(messaggio(comma_rmc(10) + 1 : comma_rmc(11) - 1));
if messaggio(comma_rmc(11) + 1) == 'W'
Magnetic_declination = -Magnetic_declination; 
end

% questi dati non sono presenti nel messaggio rmc, quindi prendo quelli
% precedenti

fix_mode = complex(out_prec(6),0);

n_sat = complex(out_prec(7),0) ;

h_mean_sea = complex(out_prec(8),0) ;

h_WGS84 = complex(out_prec(9),0) ;


%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%

% calcolo checksum

ck_ric = char(zeros(1,2)) ;
ck_ric(1) = messaggio(star(1) + uint8(1))  ;
ck_ric(2) = messaggio(star(1) + uint8(2)) ;

NMEA_rmc_string = uint8(messaggio(dollar(1)+1:star(1)-1)) ;

checksum = uint8(0) ;
for i = 1:length(NMEA_rmc_string) 
    checksum = bitxor(checksum,NMEA_rmc_string(i),'uint8') ;
end

if checksum ~= hex2dec(ck_ric) 
    Lat = out_prec(1) ;
    Long = out_prec(2) ;
    V = out_prec(3) ;
    Magnetic_declination = out_prec(4);
    heading = out_prec(5) ; 
    fix_mode = complex(out_prec(6),0);
    n_sat = complex(out_prec(7),0) ;
    h_mean_sea = complex(out_prec(8),0) ;
    h_WGS84 = complex(out_prec(9),0) ;
    indice_dimario = uint8(10);
end

%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%

% decodifica mesaggio GGA

elseif mess_type == 'GPGGA'
comma_gga = find(messaggio == ','); 
star = find(messaggio == '*');
dollar = find(messaggio == '$');


Lat_1 = str2double(messaggio(comma_gga(2) + 1 : comma_gga(2) + 2));
Lat_2 = str2double(messaggio(comma_gga(2) + 3 : comma_gga(3) - 1))/60;
Lat = Lat_1 + Lat_2;
if messaggio(comma_gga(3) + 1) == 'S'
Lat = -Lat; 
end


Long_1 = str2double(messaggio(comma_gga(4) + 1 : comma_gga(4) + 3));
Long_2 = str2double(messaggio(comma_gga(4) + 4 : comma_gga(5) - 1))/60;
Long = Long_1 + Long_2;
if messaggio(comma_gga(5) + 1) == 'W'
Long = -Long; 
end


fix_mode = str2double(messaggio(comma_gga(6) + 1)); 

n_sat = str2double(messaggio(comma_gga(7) + 1 : comma_gga(8) - 1));

h_mean_sea = str2double(messaggio(comma_gga(9) + 1 : comma_gga(10) - 1));

h_WGS84 = str2double(messaggio(comma_gga(11) + 1 : comma_gga(12) - 1));

% questi dati non sono presenti nel messaggio rmc, quindi prendo quelli
% precedenti

V = out_prec(3) ;

Magnetic_declination = out_prec(4);

heading = out_prec(5) ; 

%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%

% calcolo del checksum
ck_ric = char(zeros(1,2)) ;
ck_ric(1) = messaggio(star(1) + uint8(1))  ;
ck_ric(2) = messaggio(star(1) + uint8(2)) ;

NMEA_gga_string = uint8(messaggio(dollar(1)+1:star(1)-1)) ;

checksum = uint8(0) ;
for i = 1:length(NMEA_gga_string) 
    checksum = bitxor(checksum,NMEA_gga_string(i),'uint8') ;
end

if checksum ~= hex2dec(ck_ric) 
    Lat = out_prec(1) ;
    Long = out_prec(2) ;
    V = out_prec(3) ;
    Magnetic_declination = out_prec(4);
    heading = out_prec(5) ; 
fix_mode = complex(out_prec(6),0);

n_sat = complex(out_prec(7),0) ;

h_mean_sea = complex(out_prec(8),0) ;

h_WGS84 = complex(out_prec(9),0) ;
indice_dimario = uint8(20);


end

%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%

else

    Lat = out_prec(1) ;
    Long = out_prec(2) ;
    V = out_prec(3) ;
    Magnetic_declination = out_prec(4);
    heading = out_prec(5) ; 
fix_mode = complex(out_prec(6),0);

n_sat = complex(out_prec(7),0) ;

h_mean_sea = complex(out_prec(8),0) ;

h_WGS84 = complex(out_prec(9),0) ; 



end


else

    Lat = out_prec(1) ;
    Long = out_prec(2) ;
    V = out_prec(3) ;
    Magnetic_declination = out_prec(4);
    heading = out_prec(5) ; 
    fix_mode = complex(out_prec(6),0);
    n_sat = complex(out_prec(7),0) ;
    h_mean_sea = complex(out_prec(8),0) ;
    h_WGS84 = complex(out_prec(9),0) ; 
end

end

function [Lat_Home,Long_Home,h_Home,psi_Home] = fcn(Cal_home,Lat,Long,h,psi,GC_info,WP_AL,WP_db_info)
%#codegen

persistent Lat_H;
persistent Long_H;
persistent h_H;
persistent psi_H;
persistent counter;

if isempty( Lat_H)
    Lat_H = 0;
end

if isempty(Long_H)
   Long_H = 0;
end

if isempty(h_H)
    h_H = 0;
end

if isempty(psi_H)
    psi_H = 2.8274;        %162deg      (%2.79-160deg)
end

if isempty(counter)
    counter = 1;
end




% if Cal_home(1)==1
%     
%     Lat_H = Lat;
%     Long_H = Long;
%     h_H = h;
%     psi_H = psi;
%     
% end
% 
% if GC_info == 3
%     counter = 1;
%     for indice=1:size(WP_AL,1)
%         if WP_db_info(indice,5)==1
%             counter=indice;
%         end
%     end
% end
% 
% AL = WP_AL(counter,2);
% LatWP = WP_AL(counter,5);
% LongWP = WP_AL(counter,6);
% 
% LatWP2 = WP_AL(counter+1,5);
% LongWP2 = WP_AL(counter+1,6);    
% 
% dr=pi/180;
% 
% if AL == 1
%     Lat_Home = LatWP*dr;
%     Long_Home = LongWP*dr;
%     h_Home = WP_AL(counter,7);
%     psi_Home = atan2(sin(LongWP2*dr-LongWP*dr)*cos(LatWP2*dr),cos(LatWP*dr)*sin(LatWP2*dr)-sin(LatWP*dr)*cos(LatWP2*dr)*cos(LongWP2*dr-LongWP*dr));
% else
%     Lat_Home=Lat_H;
%     Long_Home=Long_H;
%     h_Home=h_H;
%     psi_Home=psi_H;
% end
dr=pi/180;

LatWP = WP_AL(1,5);
LongWP = WP_AL(1,6);

LatWP2 = WP_AL(2,5);
LongWP2 = WP_AL(2,6); 

Lat_Home = LatWP*dr;
Long_Home = LongWP*dr;
h_Home = WP_AL(1,7);
psi_Home = atan2(sin(LongWP2*dr-LongWP*dr)*cos(LatWP2*dr),cos(LatWP*dr)*sin(LatWP2*dr)-sin(LatWP*dr)*cos(LatWP2*dr)*cos(LongWP2*dr-LongWP*dr));

if psi_Home < 0
    psi_Home = 2*pi - abs(psi_Home);
end

function [lat_end,long_end] = fcn2(lat_ini,long_ini,heading,distance)


lat_end=zeros(1,3);
long_end=zeros(1,3);

R=6378137 ; %Earth Radius  

for n=1:3

lat_end(n) = asin(sin(lat_ini)*cos(distance(n)/R) + cos(lat_ini)*sin(distance(n)/R)*cos(heading));
long_end(n) = long_ini + atan2(sin(heading)*sin(distance(n)/R)*cos(lat_ini), cos(distance(n)/R)-sin(lat_ini)*sin(lat_end(n))); 


end


end

function [status,messaggio]  = LETTURA_Arduino_mex(byte_pack)

persistent message counter start_found

status = false;                                        %ho aggiunto questo
messaggio = uint8(zeros(1,31)) ;
M = uint8(length(byte_pack)) ;


if isempty(start_found) 
     start_found = false ;
end 
if isempty (counter) 
    counter = uint8(0) ;
end 
if isempty(message)
     message = uint8(zeros(1,31)) ;
end


if start_found == 0 
    for i = 1 : M-1  
        if ( byte_pack(i) == 255 &&  byte_pack(i + 1) == 254 )                  % mi conviene mandare un secondo byte all'inizio cosi non devo cambiare nulla
            message(1:M - (i-1)) = byte_pack(i:end) ;
            counter = M - (i-1) ; 
            start_found = true ;
            break
        end
    end   % lho messo qua
        if counter == M 
            messaggio = message ;
            message = uint8(zeros(1,31)) ;
            status = true ;
            start_found = false ;
            counter = uint8(0) ;
        end
     %end


elseif start_found == 1 

        message(counter + 1 : end) = byte_pack(1:M - counter) ;
        status = true;       %ho aggiunto questo
        messaggio = message ;
        counter = M - counter ;
        message = uint8(zeros(1,31)) ;

            for i = counter : M-1  
        if ( byte_pack(i) == 255 &&  byte_pack(i + 1) == 254 )                  % mi conviene mandare un secondo byte all'inizio cosi non devo cambiare nulla
            message(1:M - (i-1)) = byte_pack(i:end) ;
            counter = M - (i-1) ; 
            start_found = true ;
            break
        else 
            start_found = false ;
            counter = uint8(0);
        end 
            end

end





% function [status,messaggio]  = LETTURA_Rasp_mex(byte_pack)
% 
% persistent message counter start_found
% 
% status = false;                                        %ho aggiunto questo
% messaggio = uint8(zeros(1,30)) ;
% M = uint8(length(byte_pack)) ;
% 
% if isempty(start_found) 
%      start_found = false ;
% end 
% if isempty (counter) 
%     counter = uint8(0) ;
% end 
% if isempty(message)
%      message = uint8(zeros(1,30 )) ;
% end
% 
% 
% if  start_found == 0  
%     for i = 1 : M-1  
%         if ( byte_pack(i) == 255 &&  byte_pack(i + 1) == 254 ) 
%             message(1:M - (i-1)) = byte_pack(i:end) ;
%             counter = M - (i-1) ; 
%             start_found = true ;
%             break
%         end
%         if counter == M 
%             messaggio = message ;
%             message = uint8(zeros(1,30 )) ;
%             status = true ;
%             start_found = false ;
%             counter = uint8(0) ;
%         end
%      end

%     
% elseif start_found == 1 
% 
%         message(counter + 1 : end) = byte_pack(1:M - counter) ;
%         status = true;       %ho aggiunto questo
%         messaggio = message ;
%         counter = M - counter ;
%         message = uint8(zeros(1,30 )) ;
%         message (1 : M-counter ) = byte_pack(counter + 1:end) ;
%         counter = M - counter ;
% 
% end

state.P:
Allignment1
du:
maneuver_selector=1;
landing_selector=0;
trig_approach = 0;
trig_flare = 0;
approach_selector = 0;

Abort
du:
maneuver_selector=1;
landing_selector=0;
trig_approach = 0;
trig_flare = 0;
approach_selector = 0;

Allignment
du:
trig_flare = 0;
approach_selector = 0;
maneuver_selector=1;
landing_selector=0;
trig_approach = 0;

Approach

du:
trig_flare = 0;
trig_approach = 1;
approach_selector = 1;
landing_selector=1;
maneuver_selector=2;

Flare
du:
trig_approach = 0;
trig_flare = 1;
approach_selector = 0;
landing_selector=2;
maneuver_selector=3;

eml.P:
function [WP_info,WP_param] = fcn(WP_info_in,WP_param_in, GC_info)
%#codegen
WP_info=WP_info_in(1,:); %%per sicurezza
WP_param=WP_param_in(1,:); %%per sicurezza
[m,n]=size(WP_info_in);
if GC_info(1)==2
    if GC_info(2)>=0 && GC_info(2)<m %per poter inviare i MISSION_ITEM
        WP_info=WP_info_in(GC_info(2)+1,:);
        WP_param=WP_param_in(GC_info(2)+1,:);
    end
end
if GC_info(1)==3 && GC_info(2)>=0 && GC_info(2)<200 %per poter inviae i MISSION_REQUEST
    WP_info=WP_info_in(GC_info(2)+1,:);
end

function [V_vett, h_vett, rotta_vett, RC_vett, ingaggio_AP_mem, modo_pre_mem, IAS_pre_mem, ALT_pre_mem, HDG_pre_mem] = vettoriale(modo, IAS, ALT, HDG, I_RC, ingaggio_AP ,ROTTA, VELOCITA, QUOTA, RC, HEADING, TAS, h, RC_default, ingaggio_AP_pre,modo_pre, IAS_pre, ALT_pre, HDG_pre)
%#codegen

persistent V_ref ALT_ref HDG_ref

if isempty(V_ref)

    V_ref = 0;

end

if isempty(ALT_ref)

    ALT_ref = 0;

end

if isempty(HDG_ref)

    HDG_ref = 0;

end

if (ingaggio_AP ~= ingaggio_AP_pre || modo ~= modo_pre || IAS ~= IAS_pre)

    V_ref = TAS;

end

if (ingaggio_AP ~= ingaggio_AP_pre || modo ~= modo_pre || ALT ~= ALT_pre)

    ALT_ref = h;

end

if (ingaggio_AP ~= ingaggio_AP_pre || modo ~= modo_pre || HDG ~= HDG_pre)

    HDG_ref = HEADING;

end

if (IAS == 1)

    V_vett = VELOCITA;

else

    V_vett = V_ref;

end

if (ALT == 1)

    h_vett = QUOTA;

else

   h_vett = ALT_ref;

end

if (HDG == 1)

    rotta_vett = ROTTA;

else

  rotta_vett = HDG_ref;

end

RC_vett = 0;

if ((h_vett - h) >= 0) 

    if (I_RC == 1)

        RC_vett = RC;

    else

        RC_vett = RC_default;

    end

elseif ((h_vett - h) < 0 ) 

    if (I_RC == 1)

        RC_vett = -RC;

    else

        RC_vett = -RC_default;

    end 

end

ingaggio_AP_mem = uint8(ingaggio_AP);
modo_pre_mem = modo;
IAS_pre_mem  = uint8(IAS);
ALT_pre_mem  = uint8(ALT);
HDG_pre_mem  = uint8(HDG);

function h_  = fcn( T_0, P_ref,P)
%#codegen

g     = 9.81;    %accelerazione di gravità
R_gas = 287.05;  %costante dei gas perfetti
dTdh  = -0.0065; %gradiente termico verticale in atmosfera standard 

T_0        = T_0+273.15;



h_ = ((R_gas/g)*log(P_ref/P)*T_0)/(1-(dTdh*0.5*(R_gas/g)*log(P_ref/P)));

function [Mex_out,Control]  = checksum_control(Messaggio,Mex_prec)

Control = uint8(0);

mti_string = uint8(Messaggio(2:41));

checksum = sum(mti_string) ; 

hex_checksum = dec2hex(checksum);

if ((hex_checksum(3) == '0') && (hex_checksum(4) == '0'))
    Mex_out = uint8(Messaggio);

else
   Control = uint8(1); 
   Mex_out = Mex_prec;   
end



end

function [status,messaggio,messaggio_2]  = Creazione_mavlink_message_2(byte_pack,byte_available)


persistent Interr_parz  message counter len_found mess_len

if isempty(Interr_parz)
    Interr_parz = false ;
end
if isempty(message)
     message = uint8(zeros(1,100 )) ;
end
if isempty (counter) 
    counter = uint8(0) ;    % variabile che conta quanti byte mancano per completare il messaggio
end 

if isempty(len_found) 
     len_found = false ;
end 

if isempty(mess_len) 
     mess_len = 0 ;
end 

status = 0;
M = length(byte_pack) ;
caso = 0 ;
messaggio = uint8(zeros(1,100 )) ;
messaggio_2 = uint8(zeros(1,100 )) ;
% LA VARIABILE INTERR_PARZ va attivata a 1 alla fine della funzone di
% lettura per indicare di leggere il successivo messaggio in arrivo 

[poss_254,num_254] = find_254(byte_pack) ;

if ((Interr_parz == true) && (num_254 == 1))                  % in questo caso verifico che il 254 sia effettivamente un altro inizio
if (poss_254(1) ~= counter+1)
   num_254 = 0; 
end

end



if ( num_254 == 1 && Interr_parz == false )             
    caso = 1 ;                                                            % 1 Messaggio Intero   o parziale che inizia                % attivare alla fine della funzione Interr_parz == true o false 
elseif (num_254 == 0 && Interr_parz == true )
    caso = 2 ;                                                        % 1 Messaggio Parziale senza inizio e poi nulla 
elseif (num_254 == 1 && Interr_parz == true )                         
    caso = 3 ;                                                        % 1 Messaggio Parziale e poi un altro messaggio completo o parziale
elseif  ( num_254 == 2 && Interr_parz == false ) 
    caso = 4 ;                                                         % Due messaggi completi  oppure 1 Completo + parziale ( attivare Interr_parz alla fine del codice)
end


switch (caso)



    case 1 
        pos_254 = poss_254(num_254) ;

        if pos_254 == M                                                                          % se il 254 sta alla fine 
             message(1) = byte_pack(pos_254)  ;
            len_found = false ;
            Interr_parz = true ;
        else
             len = byte_pack(pos_254 + 1)  ;
             if len <= 100
             if  ((byte_pack(poss_254(1)+1) + 8) == byte_available)                                             % se abbiamo tutto il messaggio all'interno del byte_pack
                message(1: 8 + len ) = byte_pack(pos_254 : pos_254 + (7+len) ) ;          % era cosi byte_pack(pos_254 : pos_254 + (8+len) ) ;
                messaggio = message ;
                status = 1 ;
                message = uint8(zeros(1,100 )) ;
                counter = uint8(0) ;
                len_found = true ;
                mess_len = 0 ;
             elseif ((byte_pack(poss_254(1)+1) + 8) > byte_available)
                mess_len = double(byte_available) ;                                       % lunghezza del messaggio da leggere dal byte_pack ( non uguale alla lunghezza del messaggio complessivo)
                message(1 : mess_len) = byte_pack(pos_254 : mess_len ) ;
                len_found = true ;
                counter = 8 + len - mess_len ;
                Interr_parz = true ;
             end
             else
                 messaggio = uint8(zeros(1,100 )) ;              
                 Interr_parz = false;
                 message = uint8(zeros(1,100 )) ;
                 counter = uint8(0) ;
                 len_found = false ;
                 mess_len = 0 ; 
                 status = 1 ;
             end   

        end




    case 2 

                                     % se la lunghezza è stata trovata e quindi ho il valore di counter che indica quante componenti mancano
           if byte_pack ~= 0
                message(mess_len + 1 : mess_len + counter) = byte_pack( 1 : counter) ;
                Interr_parz = false ;
                messaggio = message ;
                status = 1 ;
                message = uint8(zeros(1,100 )) ;
                len_found = true ;
                mess_len = 0 ;
                counter = uint8(0) ;

           else                                                    % se ho un messaggio parziale e nello step successivo non ricevo nulla allora azzero tutto, altrimenti entro in un loop infinito di messaggi sbagliati
                Interr_parz = false ;
                messaggio = uint8(zeros(1,100)) ;
                status = 1 ;
                message = uint8(zeros(1,100 )) ;
                len_found = true ;
                mess_len = 0 ;
                counter = uint8(0) ;  
           end        





    case 3 

                                     % se la lunghezza è stata trovata e quindi ho il valore di counter che indica quante componenti mancano
        pos_254 = poss_254(num_254) ;

                message(mess_len + 1 : mess_len + counter) = byte_pack( 1 : counter) ;
                Interr_parz = false ;
                messaggio = message ;
                status = 1 ;
                message = uint8(zeros(1,100 )) ;
                len_found = true ;
                mess_len = 0 ;
                counter = uint8(0) ;

             len = byte_pack(pos_254 + 1)  ;
             if len <= 100
             if  ((byte_pack(poss_254(1)+1) + 8) == uint8(byte_available-double(counter)))                                             % se abbiamo tutto il messaggio all'interno del byte_pack
                message(1: 8 + len ) = byte_pack(pos_254 : pos_254 + (7+len) ) ;          % era cosi byte_pack(pos_254 : pos_254 + (8+len) ) ;
                messaggio_2 = message ;
                status = 1 ;
                message = uint8(zeros(1,100 )) ;
                counter = uint8(0) ;
                len_found = true ;
                mess_len = 0 ;
             elseif ((byte_pack(poss_254(1)+1) + 8) > (uint8(byte_available)-counter))
                mess_len = double(byte_available) - double(counter) ;                                       % lunghezza del messaggio da leggere dal byte_pack ( non uguale alla lunghezza del messaggio complessivo)
                message(1 : mess_len) = byte_pack(pos_254 : mess_len ) ;
                len_found = true ;
                counter = 8 + len - mess_len ;
                Interr_parz = true ;
             end
             else
                 messaggio = uint8(zeros(1,100 )) ;              
                 Interr_parz = false;
                 message = uint8(zeros(1,100 )) ;
                 counter = uint8(0) ;
                 len_found = false ;
                 mess_len = 0 ; 
                 status = 1 ;
             end









    case 4
        primo_254 = poss_254(1) ;
        secondo_254 = poss_254(2) ;
        len_primo = byte_pack(primo_254 + 1) ;
    %    messaggio(1 : len_primo + 8) = byte_pack(primo_254 : primo_254 + (len_primo + 7)) ;           %era cosi      byte_pack(primo_254 : secondo_254 - 1)  ;

        if len_primo <= 100
        messaggio(1 : len_primo + 8) = byte_pack(primo_254 : primo_254 + (len_primo + 7)) ;             %era cosi      byte_pack(primo_254 : secondo_254 - 1)  ;
        else
        messaggio = uint8(zeros(1,100 )) ;
        Interr_parz = false;
        message = uint8(zeros(1,100 )) ;
        counter = uint8(0) ;
        len_found = false ;
        mess_len = 0 ;
        end

        status = 1;

             len = byte_pack(secondo_254 + 1)  ;
             if (len <= 100 && (secondo_254-primo_254 > 3))
             if ( (byte_available - ( secondo_254))  == 7 + len    )                                         % se abbiamo tutto il messaggio all'interno del byte_pack
                message(1: 8 + len ) = byte_pack(secondo_254 : secondo_254 + (7+len) ) ;      % era cosi byte_pack(pos_254 : pos_254 + (8+len) ) ;
                messaggio_2 = message ;
                status = 2 ;
                message = uint8(zeros(1,100 )) ;
                counter = uint8(0) ;
                len_found = true ;
                mess_len = 0 ;
             else
                mess_len = length(byte_pack(secondo_254:byte_available ) ) ;    % lunghezza del messaggio da leggere dal byte_pack ( non uguale alla lunghezza del messaggio complessivo)
                message(1 : mess_len) = byte_pack(secondo_254:byte_available ) ;
                len_found = true ;
                counter = 8 + len - mess_len ;
                Interr_parz = true ;
             end
             else
               messaggio_2 = uint8(zeros(1,100 )) ;              
               Interr_parz = false;
               message = uint8(zeros(1,100 )) ;
               counter = uint8(0) ;
               len_found = false ;
               mess_len = 0 ;    
              end



end
end






function [position,counter] = find_254(pack) 
    position = zeros(1,3) ;

    M = length(pack)  ;   
    counter = 0 ;
    for i = 1 : M
        if pack(i) == 254 
            counter = counter + 1;  
            position(counter) = i; 
        end
    end
    l = pack(position(1)+1);
    if counter > 1
    if ((pack(l+8+position(1)) ~= 254) && (position(1) ~= 0))
        counter = 1;
        position(2) = 0;
    end
    end
end

function [status,check,messaggio]  = LETTURA_Rasp_mex(byte_pack,mess_prec)


status = false;   
check = false;
messaggio = uint8(zeros(1,27)) ;
M = uint8(length(byte_pack)) ;



if ( (byte_pack(1) == 255) &&  (byte_pack(2) == 254) && (byte_pack(M) == 60))
    messaggio = byte_pack;
    status = true;
    check = false;
else
    messaggio = mess_prec;
    check = true;
    status = true;
end

